import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { FormsModule } from '@angular/forms';
import { NgModule } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  title = 'Reto-3';
  visible = true;
  visible2 = true;

  ocultar(){
    this.visible = false;
  }
  ocultar2(){
    this.visible2 = false;
  }




}



<nav *ngIf="visible" class="navbar navbar-expand-lg bg-body-tertiary colornavbar">
    <div class="container-fluid">
      <a class="navbar-brand enlaceInicio" href="#">ONG</a>
      <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
        <span class="navbar-toggler-icon"></span>
      </button>
      <div class="collapse navbar-collapse" id="navbarSupportedContent">
        <ul class="navbar-nav me-auto mb-2 mb-lg-0">


          <li class="nav-item">
            <a class="nav-link" routerLink="/Form-donacion" (click)="ocultar2()" >Donar</a>
          </li>
          <li class="nav-item">
            <a class="nav-link" routerLink="/SobreNosotros" (click)="ocultar2()">Sobre Nosotros</a>
          </li>
          <li class="nav-item">
            <a class="nav-link"  routerLink="/Login"(click)="ocultar()" (click)="ocultar2()">Login</a>


        </ul>
        <form class="d-flex" role="search">

        </form>
      </div>
    </div>
  </nav>
<div *ngIf="visible2">
   <app-home></app-home>
</div>


<router-outlet></router-outlet>
