import { Component } from '@angular/core';
import { FormsModule } from '@angular/forms';

@Component({
  selector: 'app-rescatados-publi',
  templateUrl: './rescatados-publi.component.html',
  styleUrls: ['./rescatados-publi.component.scss']
})
export class RescatadosPubliComponent {
  patternDni = '^[0-9]{8}[A-Z]$';
  dni: string = "";

  onSubmit(form: any) {
    console.log(form.value);
  }
}
<form #form="ngForm" (ngSubmit)="onSubmit(form)">

  Nombre: <input type="text" name="nombre" ngModel="nombre" /><br>

  DNI: <input type="text" name="dni" [(ngModel)]="dni" placeholder="obligatorio" required [pattern]="patternDni" /><br>

  <div *ngIf="form.submitted || form.controls['dni']?.touched">
    <div *ngIf="form.controls['dni']?.hasError('required')">
      El DNI es obligatorio.
    </div>
    <div *ngIf="form.controls['dni']?.hasError('pattern')">
      El DNI debe tener al menos 8 números y 1 letra al final.
    </div>
  </div>

  Metodo de pago:<br>
  <input type="radio" ngModel="pago" name="medotoPago" value="tarjeta" required />Tarjeta<br>
  <input type="radio" ngModel="pago" name="medotoPago" value="paypal" />Paypal<br>
  <input type="radio" ngModel="pago" name="medotoPago" value="numero-cuenta" />Numero de cuenta<br>

  <button type="submit">ENVIAR</button>

</form>

